import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleCheck,
  faPen,
  faTrashCan,
} from "@fortawesome/free-solid-svg-icons";
import "./App.css";

function App() {
  const [toDo, setTodo] = useState([
    { id: 1, title: "Learn React", status: false },
    { id: 2, title: "Learn Redux", status: false },
  ]);

  const [newTask, setNewtask] = useState("");
  const [updateTask, setUpdatetask] = useState("");

  //functions

  const addTask = () => {
    if (newTask) {
      const newTaskObj = {
        id: Date.now(),
        title: newTask,
        completed: false,
      };
      setTodo([...toDo, newTaskObj]);
      setNewtask("");
    }
  };

  const deleteTask = (id) => {
    const newTasks = toDo.filter((task) => task.id !== id);
    setTodo(newTasks);
  };

  const completeTask = (id) => {
    const newTask = toDo.map((task) => {
      if(task.id === id) {
        return { ...task, status: !task.status };
      }
      return task;
    });
    setTodo(newTask);
  };

  const cancelUpdate = () => {
    setUpdatetask("");
  };


   // Update task

  const updatenewTask = () => {
    let filterRecords = [...toDo].filter( task => task.id !== updateTask.id );
    let updatedObject = [...filterRecords, updateTask]
    setTodo(updatedObject);
    setUpdatetask('');
  }


  return (
    <div className="container App">
      <br></br>
      <h2>ToDo App</h2>
      <br></br>


      {updatenewTask && updatenewTask ? (
        <>
          <div className="row">
            <div className="col">
              <input 
                value={updateTask && updateTask.title} 
                onChange={ (e) => updatenewTask(e) } 
                className="form-control form-control-lg" 
              />
            </div>
            <div className="col-auto">
              <button 
                className="btn btn-lg btn-success" 
                onClick={updatenewTask}
              >Update</button>
              <button 
                className="btn btn-lg btn-warning" 
                onClick={cancelUpdate}
              >Cancel</button>
            </div>
          </div>
          <br />
        </>
      ) : (
        <>
          <div className="row">
            <div className="col">
              <input 
                value={newTask} 
                onChange={e => setNewtask(e.target.value)} 
                className="form-control form-control-lg" 
              />
            </div>
            <div className="col-auto">
              <button 
                className="btn btn-lg btn-success" 
                onClick={addTask}
              >Add Task</button>
            </div>
          </div>
          <br />
        </>
      )}

        {toDo && toDo.length ? "" : "No tasks available"}

        {toDo &&
          toDo.map((task, index) => {
            return (
              <React.Fragment key={task.id}>
                <div className="col taskBg">
                  <div className={task.status ? "done" : ""}>
                    <span className="taskNum">{index + 1}</span>
                    <span className="taskName">{task.title}</span>
                  </div>
                  <div className="icon">
                    <span
                      title="Make as done"
                      onClick={() => completeTask(task.id)}
                    >
                      <FontAwesomeIcon icon={faCircleCheck} />
                    </span>
                    {task.status ? null : (
                  <span 
                    title="Edit"
                    onClick={ () => setUpdatetask({ id: task.id, title: task.title, satus: task.status ? true : false }) }
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </span>
                )}

                    <span title="Delete" onClick={() => deleteTask(task.id)}>
                      <FontAwesomeIcon icon={faTrashCan} />
                    </span>
                  </div>
                </div>

        </React.Fragment>
        );
      })}
    </div>
  );
}

export default App;
