import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleCheck,
  faPen,
  faTrashCan,
} from "@fortawesome/free-solid-svg-icons";
import "./App.css";

function App() {
  const [toDo, setTodo] = useState([
    { id: 1, title: "Learn React", status: false },
    { id: 2, title: "Learn Redux", status: false },
  ]);

  const [newTask, setNewtask] = useState("");
  const [updateTask, setUpdatetask] = useState("");

  //functions

  const addTask = () => {
    if (newTask) {
      const newTaskObj = {
        id: Date.now(),
        title: newTask,
        completed: false,
      };
      setTodo([...toDo, newTaskObj]);
      setNewtask("");
    }
  };



  const deleteTask = (id) => {
    const newTasks = toDo.filter((task) => task.id !== id);
    setTodo(newTasks);
  };

  const completeTask = (id) => {
    const newTask = toDo.map((task) => {
      if(task.id === id) {
        return { ...task, status: !task.status };
      }
      return task;
    });
    setTodo(newTask);
  };

  const cancelUpdate = () => {
    setUpdatetask("");
  };

  const changeUpdate = (e) => {
    let newEntry = {
      id: updateTask.id,
      title: e.target.value,
      status: updateTask.status ? true : false,
    }
    setUpdatetask(newEntry);
  };

   // Update task

  const updatenewTask = () => {
    let filterRecords = [...toDo].filter( task => task.id !== updateTask.id );
    let updatedObject = [...filterRecords, updateTask]
    setTodo(updatedObject);
    setUpdatetask('');
  }


  return (
    <div className="container App">
      <br></br>
      <h2>ToDo App</h2>
      <br></br>

      {updateData && updateData ? (
        <>
          <div className="row">
            <div className="col">
              <input 
                value={updateData && updateData.title} 
                onChange={ (e) => changeTask(e) } 
                className="form-control form-control-lg" 
              />
            </div>
            <div className="col-auto">
              <button 
                className="btn btn-lg btn-success mr-20" 
                onClick={updateTask}
              >Update</button>
              <button 
                className="btn btn-lg btn-warning" 
                onClick={cancelUpdate}
              >Cancel</button>
            </div>
          </div>
          <br />
        </>
      ) : (
        <>
          <div className="row">
            <div className="col">
              <input 
                value={newTask} 
                onChange={e => setNewTask(e.target.value)} 
                className="form-control form-control-lg" 
              />
            </div>
            <div className="col-auto">
              <button 
                className="btn btn-lg btn-success" 
                onClick={addTask}
              >Add Task</button>
            </div>
          </div>
          <br />
        </>
      )}


      {/* If there are no to dos in state, display a message   */}
      {toDo && toDo.length ? '' : 'No tasks...'}

      {/* Show to dos   */}
      {toDo && toDo
        .sort((a, b) => a.id > b.id ? 1 : -1)
        .map( (task, index) => {
        return(
          <React.Fragment key={task.id}>

            <div className="col taskBg">

              <div 
                // if task status is true, add class to this div named as done
                className={ task.status ? 'done' : '' }
              >
                {/* Show number of task */}
                <span className="taskNumber">{index + 1}</span> 
                <span className="taskText">{task.title}</span>
              </div>

              <div className="iconsWrap">
                <span 
                  onClick={(e) => markDone(task.id)}
                  title="Completed / Not Completed"
                >
                  <FontAwesomeIcon icon={faCircleCheck} />
                </span>

                {task.status ? null : (
                  <span 
                    title="Edit"
                    onClick={ () => setUpdateData({ id: task.id, title: task.title, satus: task.status ? true : false }) }
                  >
                    <FontAwesomeIcon icon={faPen} />
                  </span>
                )}

                <span 
                  onClick={() => deleteTask(task.id)}
                  title="Delete"
                >
                  <FontAwesomeIcon icon={faTrashCan} />
                </span>
              </div>

            </div>

        </React.Fragment>
        );
      })}
    </div>
  );
}

export default App;
