import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleCheck,
  faPen,
  faTrashCan,
} from "@fortawesome/free-solid-svg-icons";
import "./App.css";

function App() {
  const [toDo, setTodo] = useState([
    { id: 1, title: "Learn React", status: false },
    { id: 2, title: "Learn Redux", status: false },
  ]);

  const [newTask, setNewtask] = useState("");
  const [updateTask, setUpdatetask] = useState("");

  //functions

  const addTask = () => {
    if (newTask) {
      const newTaskObj = {
        id: Date.now(),
        title: newTask,
        completed: false,
      };
      setTodo([...toDo, newTaskObj]);
      setNewtask("");
    }
  };

  const updatenewTask = () => {
    if (updateTask) {
      const updateTaskObj = {
        id: Date.now(),
        title: updateTask,
        completed: false,
      };
      setTodo([...toDo, updateTaskObj]);
      setUpdatetask("");
    }
  };

  const deleteTask = (id) => {
    const newTasks = toDo.filter((task) => task.id !== id);
    setTodo(newTasks);
  };

  const completeTask = (id) => {
    const newTask = toDo.map((task) => {
      if (task.id === id) {
        return { ...task, status: !task.status };
      }
      return task;
    });
    setTodo(newTask);
  };

  const cancelUpdate = (
      ) => {};

  const changeUpdate = (e) => {};

  return (
    <div className="container App">
      <br></br>
      <h2>ToDo App</h2>
      <br></br>

      <div className="row">
        <div className="col">
          <input
            type="text"
            className="form-control"
            placeholder="Add new task"
            value={newTask}
            onChange={(e) => setNewtask(e.target.value)}
          />
        </div>

        <div className="col">
          <button className="btn btn-primary" onClick={addTask}>
            Add
          </button>
        </div>

        <div className="col">
          <input
            type="text" 
            className="form-control"
            placeholder="Update task"
            value={updateTask}
            onChange={changeUpdate}
          />
        </div>


        <div className="col">
          <button className="btn btn-primary" onClick={updatenewTask}>
            Update
          </button>
        </div>

        <div className="col">
          <button className="btn btn-primary" onClick={cancelUpdate}>
            Cancel
          </button>
        </div>



      {toDo && toDo.length ? "" : "No tasks available"}

      {toDo &&
        toDo.map((task, index) => {
          return (
            <React.Fragment key={task.id}>
              <div className="col taskBg">
                <div className={task.status ? "done" : ""}>
                  <span className="taskNum">{index + 1}</span>
                  <span className="taskName">{task.title}</span>
                </div>
                <div className="icon">
                  <span title="Make as done"
                    onClick={() => completeTask(task.id)}
                  >
                    <FontAwesomeIcon icon={faCircleCheck} />
                  </span>
                  {task.status ? "" : (
                    <span title="Edit">
                    <FontAwesomeIcon icon={faPen} />
                  </span>
                  )}

                  <span title="Delete"
                    onClick={() => deleteTask(task.id)}
                  >
                    <FontAwesomeIcon icon={faTrashCan} />
                  </span>
                </div>
              </div>
            </React.Fragment>
          );
        })}
    </div>
    </div>
  );
}

export default App;
